// OpenAIAPIVersion is the API version for the Azure OpenAI API.
// ref: https://learn.microsoft.com/en-us/azure/ai-services/openai/api-version-deprecation#latest-ga-api-release
const OpenAIAPIVersion = "2024-10-21"

// OpenAIOSeriesAPIVersion is the API version for the Azure OpenAI O-series API.
// ref: https://learn.microsoft.com/en-us/azure/ai-services/openai/api-version-deprecation#latest-ga-api-release
const OpenAIOSeriesAPIVersion = "2024-12-01-preview"

// OseriesModel is a regular expression to match the O-series model names.
// ref: https://learn.microsoft.com/en-us/azure/ai-services/openai/how-to/reasoning?tabs=python-secure
// only support o3-mini, o1, o1-preview, o1-mini

var supportedOSeriesModels = []string{
	"o1", "o1-preview", "o1-mini",
	"o3-mini", "aide-o3", "aide-o3-pro",
	"aide-o4-mini",
}

var OseriesModel = regexp.MustCompile(fmt.Sprintf("(%s)", strings.Join(supportedOSeriesModels, "|")))

