     1→package azure
     2→
     3→import (
     4→	"context"
     5→	"encoding/json"
     6→	"errors"
     7→	"fmt"
     8→	"net/http"
     9→	"strings"
    10→
    11→	"gitea.mediatek.inc/davinci/azure-gateway/common"
    12→	"gitea.mediatek.inc/davinci/azure-gateway/crowdadapter"
    13→	"gitea.mediatek.inc/davinci/azure-gateway/quota"
    14→	"github.com/pkoukk/tiktoken-go"
    15→	"golang.org/x/exp/slog"
    16→)
    17→
    18→// Error definitions for Azure OpenAI operations
    19→var (
    20→	ErrTokenLimit        = errors.New("your input exceeds the token limit")
    21→	VisionStreamDisabled = errors.New("streaming gpt-4-turbo-vision responses is disabled")
    22→	VisionNotAllowed     = errors.New("you are not allowed to use gpt-4-turbo-vision")
    23→	GPT4NotAllowed       = errors.New("you are not allowed to use gpt-4")
    24→	DomainNotAllowed     = errors.New("you are not allowed to use the model in this domain")
    25→)
    26→
    27→// AoaiResp represents the response structure from the Azure OpenAI API.
    28→// It contains usage information about the tokens consumed during the request.
    29→type AoaiResp struct {
    30→	Usage struct {
    31→		// PromptTokens represents the number of tokens used in the prompt.
    32→		// This field is used in the original response format.
    33→		PromptTokens int `json:"prompt_tokens"`
    34→
    35→		// CompletionTokens represents the number of tokens used in the completion.
    36→		// This field is used in the original response format.
    37→		CompletionTokens int `json:"completion_tokens"`
    38→
    39→		// InputTokens represents the number of tokens used in the input.
    40→		// This field is used in the new response format.
    41→		InputTokens int `json:"input_tokens"`
    42→
    43→		// OutputTokens represents the number of tokens used in the output.
    44→		// This field is used in the new response format.
    45→		OutputTokens int `json:"output_tokens"`
    46→	} `json:"usage"`
    47→}
    48→
    49→// DalleReq represents a DALL-E image generation request
    50→type DalleReq struct {
    51→	N int `json:"n"`
    52→}
    53→
    54→// Message represents a message in a chat completion request
    55→type Message struct {
    56→	Role    string          `json:"role"`
    57→	Content json.RawMessage `json:"content"`
    58→}
    59→
    60→// MessageContent represents the content structure of a message
    61→type MessageContent struct {
    62→	Type string `json:"type"`
    63→	Text string `json:"text"`
    64→}
    65→
    66→// HasImages checks if the message contains image content
    67→func (m Message) HasImages() bool {
    68→	var content []MessageContent
    69→	if json.Unmarshal(m.Content, &content) == nil {
    70→		for _, c := range content {
    71→			if c.Type == "image_url" {
    72→				return true
    73→			}
    74→		}
    75→	}
    76→	return false
    77→}
    78→
    79→// SplitImageMessages separates text and image content from a message
    80→func (m Message) SplitImageMessages() (Message, int) {
    81→	if !m.HasImages() {
    82→		return m, 0
    83→	}
    84→	var oldContents, newContents []MessageContent
    85→	imageCount := 0
    86→	if json.Unmarshal(m.Content, &oldContents) == nil {
    87→		for _, c := range oldContents {
    88→			if c.Type == "image_url" {
    89→				imageCount++
    90→				continue
    91→			}
    92→			newContents = append(newContents, c)
    93→		}
    94→	}
    95→	var newMessage Message
    96→	newMessage.Role = m.Role
    97→	newMessage.Content, _ = json.Marshal(newContents)
    98→
    99→	return newMessage, imageCount
   100→}
   101→
   102→// StreamBody represents a streaming request body
   103→type StreamBody struct {
   104→	Stream   bool      `json:"stream"`
   105→	Messages []Message `json:"messages"`
   106→}
   107→
   108→// CountToken counts the tokens in the request, accounting for images
   109→func (sb StreamBody) CountToken(encoder *tiktoken.Tiktoken, fallbackTokens int) int {
   110→	hasImages := false
   111→	for _, m := range sb.Messages {
   112→		if m.HasImages() {
   113→			hasImages = true
   114→			break
   115→		}
   116→	}
   117→
   118→	if !hasImages {
   119→		body, err := json.Marshal(sb)
   120→		if err != nil {
   121→			return fallbackTokens
   122→		}
   123→		return len(encoder.Encode(string(body), nil, nil))
   124→	}
   125→
   126→	totalImageCount := 0
   127→	var textContents []Message
   128→	for _, m := range sb.Messages {
   129→		if m.HasImages() {
   130→			textContent, imageCount := m.SplitImageMessages()
   131→			textContents = append(textContents, textContent)
   132→			totalImageCount += imageCount
   133→		} else {
   134→			textContents = append(textContents, m)
   135→		}
   136→	}
   137→
   138→	textBody, err := json.Marshal(textContents)
   139→	if err != nil {
   140→		return fallbackTokens
   141→	}
   142→	textToken := len(encoder.Encode(string(textBody), nil, nil))
   143→	// https://learn.microsoft.com/en-us/azure/ai-services/openai/overview#image-tokens-gpt-4-turbo-with-vision
   144→	// max image token = 170 * 8 + 85 = 1445
   145→	imageToken := 1445 * totalImageCount
   146→
   147→	return textToken + imageToken
   148→}
