   150→// AoaiSpendFn creates a spend function for Azure OpenAI models
   151→func AoaiSpendFn(model string, capgpt4 bool, crowdapi crowdadapter.Adapter, qc quota.QuotaClient, smtpserver string, encoder *tiktoken.Tiktoken, promptrate, completionrate int) common.SpendFn {
   152→	return common.SpendFn{
   153→		Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
   154→			var checkCrowd bool
   155→			var groups []crowdadapter.CrowdGroup
   156→			if _, ok := crowdapi.(*crowdadapter.CrowdAPI); ok {
   157→				checkCrowd = true
   158→			}
   159→			if checkCrowd && (common.IsAirgappedDomain(domain) || common.IsAirgappedDomain(req.Header.Get("X-Domain"))) {
   160→				groups, err = crowdapi.GetUserNestedGroups(req.Context(), user, crowdadapter.DefaultLimit)
   161→				if err != nil {
   162→					return 0, 0, err
   163→				}
   164→				permit := false
   165→				group := "gaisf_" + domain + "_" + strings.ReplaceAll(model, "-", "_")
   166→				for _, g := range groups {
   167→					if g.Name == group {
   168→						permit = true
   169→						break
   170→					}
   171→				}
   172→				if !permit {
   173→					return 0, 0, fmt.Errorf("%w: %s", DomainNotAllowed, group)
   174→				}
   175→			}
   176→
   177→			var tokens int
   178→			if strings.Contains(model, "gpt-4o") || strings.Contains(model, "gpt-4.1") || strings.Contains(model, "vision") {
   179→				tokens = 2000
   180→				var sb StreamBody
   181→				if err := json.Unmarshal([]byte(body), &sb); err != nil {
   182→					return 0, 0, err
   183→				}
   184→				tokens = sb.CountToken(encoder, tokens)
   185→			} else {
   186→				tokens = len(encoder.Encode(body, nil, nil))
   187→			}
   188→
   189→			if checkCrowd && capgpt4 && strings.Contains(model, "aide-gpt-4") {
   190→				if groups == nil {
   191→					groups, err = crowdapi.GetUserNestedGroups(req.Context(), user, crowdadapter.DefaultLimit)
   192→					if err != nil {
   193→						return 0, 0, err
   194→					}
   195→				}
   196→
   197→				if model == "aide-gpt-4-turbo-vision" {
   198→					permit := false
   199→					if domain == "oa" {
   200→					L3:
   201→						for _, g := range groups {
   202→							switch g.Name {
   203→							case "gaisf_io_gpt4_vision", "gaisf_oa_gpt4_vision", "gaisf_oa_gpt4_turbo_vision":
   204→								permit = true
   205→								break L3
   206→							}
   207→						}
   208→					} else if domain == "swrd" {
   209→					L4:
   210→						for _, g := range groups {
   211→							switch g.Name {
   212→							case "gaisf_swrd_gpt4_vision", "gaisf_swrd_gpt4_turbo_vision":
   213→								permit = true
   214→								break L4
   215→							}
   216→						}
   217→					}
   218→					if !permit {
   219→						return 0, 0, VisionNotAllowed
   220→					}
   221→				} else if (tokens > int(8000*1.1) && (model == "aide-gpt-4-turbo")) ||
   222→					(tokens > int(16000*1.1) && (model == "aide-gpt-4o")) ||
   223→					(tokens > int(16000*1.1) && (model == "aide-gpt-4o-mini")) {
   224→					if !common.CheckTokenLimitPermision(domain, tokens, groups) {
   225→						return 0, 0, ErrTokenLimit
   226→					}
   227→				}
   228→			}
   229→			estimate = tokens * promptrate
   230→			remain, err = qc.Spend(context.Background(), user, estimate)
   231→			return
   232→		},
   233→		Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
   234→			if resp == nil {
   235→				estimate = preestimate * -1
   236→			} else if preestimate > 0 && resp.StatusCode >= 300 {
   237→				estimate = preestimate * -1
   238→			} else if strings.Contains(resp.Header.Get("content-type"), "text/event-stream") {
   239→				estimate = strings.Count(body, "\ndata: {") * completionrate
   240→			} else {
   241→				var resp AoaiResp
   242→				if err := json.Unmarshal([]byte(body), &resp); err == nil {
   243→					if resp.Usage.PromptTokens > 0 && resp.Usage.CompletionTokens > 0 {
   244→						estimate = completionrate*resp.Usage.CompletionTokens + promptrate*resp.Usage.PromptTokens - preestimate
   245→					} else if resp.Usage.InputTokens > 0 && resp.Usage.OutputTokens > 0 {
   246→						estimate = completionrate*resp.Usage.OutputTokens + promptrate*resp.Usage.InputTokens - preestimate
   247→					} else {
   248→						estimate = preestimate * -1
   249→					}
   250→				} else {
   251→					estimate = preestimate * -1
   252→				}
   253→			}
   254→			if estimate != 0 {
   255→				qc.Spend(context.Background(), user, estimate)
   256→			}
   257→			return
   258→		},
   259→		Alert: common.AlertFn(smtpserver, qc),
   260→	}
   261→}
