package azure

import (
	"context"
	"encoding/json"
	"errors"
	"fmt"
	"net/http"
	"strings"

	"gitea.mediatek.inc/davinci/azure-gateway/common"
	"gitea.mediatek.inc/davinci/azure-gateway/crowdadapter"
	"gitea.mediatek.inc/davinci/azure-gateway/quota"
	"github.com/pkoukk/tiktoken-go"
	"golang.org/x/exp/slog"
)

// Error definitions for Azure OpenAI operations
var (
	ErrTokenLimit        = errors.New("your input exceeds the token limit")
	VisionStreamDisabled = errors.New("streaming gpt-4-turbo-vision responses is disabled")
	VisionNotAllowed     = errors.New("you are not allowed to use gpt-4-turbo-vision")
	GPT4NotAllowed       = errors.New("you are not allowed to use gpt-4")
	DomainNotAllowed     = errors.New("you are not allowed to use the model in this domain")
)

// AoaiResp represents the response structure from the Azure OpenAI API.
// It contains usage information about the tokens consumed during the request.
type AoaiResp struct {
	Usage struct {
		// PromptTokens represents the number of tokens used in the prompt.
		// This field is used in the original response format.
		PromptTokens int `json:"prompt_tokens"`

		// CompletionTokens represents the number of tokens used in the completion.
		// This field is used in the original response format.
		CompletionTokens int `json:"completion_tokens"`

		// InputTokens represents the number of tokens used in the input.
		// This field is used in the new response format.
		InputTokens int `json:"input_tokens"`

		// OutputTokens represents the number of tokens used in the output.
		// This field is used in the new response format.
		OutputTokens int `json:"output_tokens"`
	} `json:"usage"`
}

// DalleReq represents a DALL-E image generation request
type DalleReq struct {
	N int `json:"n"`
}

// Message represents a message in a chat completion request
type Message struct {
	Role    string          `json:"role"`
	Content json.RawMessage `json:"content"`
}

// MessageContent represents the content structure of a message
type MessageContent struct {
	Type string `json:"type"`
	Text string `json:"text"`
}

// HasImages checks if the message contains image content
func (m Message) HasImages() bool {
	var content []MessageContent
	if json.Unmarshal(m.Content, &content) == nil {
		for _, c := range content {
			if c.Type == "image_url" {
				return true
			}
		}
	}
	return false
}

// SplitImageMessages separates text and image content from a message
func (m Message) SplitImageMessages() (Message, int) {
	if !m.HasImages() {
		return m, 0
	}
	var oldContents, newContents []MessageContent
	imageCount := 0
	if json.Unmarshal(m.Content, &oldContents) == nil {
		for _, c := range oldContents {
			if c.Type == "image_url" {
				imageCount++
				continue
			}
			newContents = append(newContents, c)
		}
	}
	var newMessage Message
	newMessage.Role = m.Role
	newMessage.Content, _ = json.Marshal(newContents)

	return newMessage, imageCount
}

// StreamBody represents a streaming request body
type StreamBody struct {
	Stream   bool      `json:"stream"`
	Messages []Message `json:"messages"`
}

// CountToken counts the tokens in the request, accounting for images
func (sb StreamBody) CountToken(encoder *tiktoken.Tiktoken, fallbackTokens int) int {
	hasImages := false
	for _, m := range sb.Messages {
		if m.HasImages() {
			hasImages = true
			break
		}
	}

	if !hasImages {
		body, err := json.Marshal(sb)
		if err != nil {
			return fallbackTokens
		}
		return len(encoder.Encode(string(body), nil, nil))
	}

	totalImageCount := 0
	var textContents []Message
	for _, m := range sb.Messages {
		if m.HasImages() {
			textContent, imageCount := m.SplitImageMessages()
			textContents = append(textContents, textContent)
			totalImageCount += imageCount
		} else {
			textContents = append(textContents, m)
		}
	}

	textBody, err := json.Marshal(textContents)
	if err != nil {
		return fallbackTokens
	}
	textToken := len(encoder.Encode(string(textBody), nil, nil))
	// https://learn.microsoft.com/en-us/azure/ai-services/openai/overview#image-tokens-gpt-4-turbo-with-vision
	// max image token = 170 * 8 + 85 = 1445
	imageToken := 1445 * totalImageCount

	return textToken + imageToken
}

// AoaiSpendFn creates a spend function for Azure OpenAI models
func AoaiSpendFn(model string, capgpt4 bool, crowdapi crowdadapter.Adapter, qc quota.QuotaClient, smtpserver string, encoder *tiktoken.Tiktoken, promptrate, completionrate int) common.SpendFn {
	return common.SpendFn{
		Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
			var checkCrowd bool
			var groups []crowdadapter.CrowdGroup
			if _, ok := crowdapi.(*crowdadapter.CrowdAPI); ok {
				checkCrowd = true
			}
			if checkCrowd && (common.IsAirgappedDomain(domain) || common.IsAirgappedDomain(req.Header.Get("X-Domain"))) {
				groups, err = crowdapi.GetUserNestedGroups(req.Context(), user, crowdadapter.DefaultLimit)
				if err != nil {
					return 0, 0, err
				}
				permit := false
				group := "gaisf_" + domain + "_" + strings.ReplaceAll(model, "-", "_")
				for _, g := range groups {
					if g.Name == group {
						permit = true
						break
					}
				}
				if !permit {
					return 0, 0, fmt.Errorf("%w: %s", DomainNotAllowed, group)
				}
			}

			var tokens int
			if strings.Contains(model, "gpt-4o") || strings.Contains(model, "gpt-4.1") || strings.Contains(model, "vision") {
				tokens = 2000
				var sb StreamBody
				if err := json.Unmarshal([]byte(body), &sb); err != nil {
					return 0, 0, err
				}
				tokens = sb.CountToken(encoder, tokens)
			} else {
				tokens = len(encoder.Encode(body, nil, nil))
			}

			if checkCrowd && capgpt4 && strings.Contains(model, "aide-gpt-4") {
				if groups == nil {
					groups, err = crowdapi.GetUserNestedGroups(req.Context(), user, crowdadapter.DefaultLimit)
					if err != nil {
						return 0, 0, err
					}
				}

				if model == "aide-gpt-4-turbo-vision" {
					permit := false
					if domain == "oa" {
					L3:
						for _, g := range groups {
							switch g.Name {
							case "gaisf_io_gpt4_vision", "gaisf_oa_gpt4_vision", "gaisf_oa_gpt4_turbo_vision":
								permit = true
								break L3
							}
						}
					} else if domain == "swrd" {
					L4:
						for _, g := range groups {
							switch g.Name {
							case "gaisf_swrd_gpt4_vision", "gaisf_swrd_gpt4_turbo_vision":
								permit = true
								break L4
							}
						}
					}
					if !permit {
						return 0, 0, VisionNotAllowed
					}
				} else if (tokens > int(8000*1.1) && (model == "aide-gpt-4-turbo")) ||
					(tokens > int(16000*1.1) && (model == "aide-gpt-4o")) ||
					(tokens > int(16000*1.1) && (model == "aide-gpt-4o-mini")) {
					if !common.CheckTokenLimitPermision(domain, tokens, groups) {
						return 0, 0, ErrTokenLimit
					}
				}
			}
			estimate = tokens * promptrate
			remain, err = qc.Spend(context.Background(), user, estimate)
			return
		},
		Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
			if resp == nil {
				estimate = preestimate * -1
			} else if preestimate > 0 && resp.StatusCode >= 300 {
				estimate = preestimate * -1
			} else if strings.Contains(resp.Header.Get("content-type"), "text/event-stream") {
				estimate = strings.Count(body, "\ndata: {") * completionrate
			} else {
				var resp AoaiResp
				if err := json.Unmarshal([]byte(body), &resp); err == nil {
					if resp.Usage.PromptTokens > 0 && resp.Usage.CompletionTokens > 0 {
						estimate = completionrate*resp.Usage.CompletionTokens + promptrate*resp.Usage.PromptTokens - preestimate
					} else if resp.Usage.InputTokens > 0 && resp.Usage.OutputTokens > 0 {
						estimate = completionrate*resp.Usage.OutputTokens + promptrate*resp.Usage.InputTokens - preestimate
					} else {
						estimate = preestimate * -1
					}
				} else {
					estimate = preestimate * -1
				}
			}
			if estimate != 0 {
				qc.Spend(context.Background(), user, estimate)
			}
			return
		},
		Alert: common.AlertFn(smtpserver, qc),
	}
}

// DalleSpendFn creates a spend function for DALL-E image generation
func DalleSpendFn(costPerImage int, qc quota.QuotaClient, smtpserver string) common.SpendFn {
	return common.SpendFn{
		Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
			var dreq DalleReq
			if err := json.Unmarshal([]byte(body), &dreq); err == nil {
				estimate = dreq.N * costPerImage
			}
			remain, err = qc.Spend(context.Background(), user, estimate)
			return
		},
		Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
			if resp != nil && preestimate > 0 && resp.StatusCode >= 300 {
				estimate = preestimate * -1
				qc.Spend(context.Background(), user, estimate)
			}
			return 0
		},
		Alert: func(user string, logger *slog.Logger) {},
	}
}

// WhisperSpendFn creates a spend function for Whisper speech-to-text
func WhisperSpendFn(qc quota.QuotaClient) common.SpendFn {
	return common.SpendFn{
		Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
			estimate = 6000000 // assume 10 minute
			remain, err = qc.Spend(context.Background(), user, estimate)
			return
		},
		Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
			if resp != nil && preestimate > 0 && resp.StatusCode >= 300 {
				estimate = preestimate * -1
				qc.Spend(context.Background(), user, estimate)
			}
			return 0
		},
		Alert: func(user string, logger *slog.Logger) {},
	}
}

// TTSSpendFn creates a spend function for text-to-speech
func TTSSpendFn(costEstimate int, qc quota.QuotaClient) common.SpendFn {
	return common.SpendFn{
		Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
			estimate = costEstimate // e.g., 15000000 for TTS, 30000000 for TTS-HD
			remain, err = qc.Spend(context.Background(), user, estimate)
			return
		},
		Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
			if resp != nil && preestimate > 0 && resp.StatusCode >= 300 {
				estimate = preestimate * -1
				qc.Spend(context.Background(), user, estimate)
			}
			return
		},
		Alert: func(user string, logger *slog.Logger) {},
	}
}

// RealtimeSpendFn creates a spend function for realtime models (no quota tracking)
func RealtimeSpendFn() common.SpendFn {
	return common.SpendFn{
		Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
			return
		},
		Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
			return
		},
		Alert: func(user string, logger *slog.Logger) {},
	}
}

// AssistantsSpendFn creates a spend function for Assistants API
func AssistantsSpendFn(qc quota.QuotaClient, smtpserver string) common.SpendFn {
	return common.SpendFn{
		Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
			if req.Method == http.MethodPost && strings.Contains(req.URL.Path, "/runs") {
				estimate = 1000000 // estimated assistants api costs $10 for 1000 runs
				remain, err = qc.Spend(context.Background(), user, estimate)
				return
			}
			return 0, 1, nil
		},
		Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
			if resp != nil && preestimate > 0 && resp.StatusCode >= 300 {
				qc.Spend(context.Background(), user, preestimate*-1)
				return preestimate * -1
			}
			return 0
		},
		Alert: common.AlertFn(smtpserver, qc),
	}
}

