package azure

import (
	"testing"
)

func TestRegexOseriesModel(t *testing.T) {
	tests := []struct {
		name  string
		input string
		want  bool
	}{
		{
			name:  "o3-mini should match",
			input: "o3-mini",
			want:  true,
		},
		{
			name:  "o1 should match",
			input: "o1",
			want:  true,
		},
		{
			name:  "o1-preview should match",
			input: "o1-preview",
			want:  true,
		},
		{
			name:  "o1-mini should match",
			input: "o1-mini",
			want:  true,
		},
		{
			name:  "o2 should not match",
			input: "o2",
			want:  false,
		},
		{
			name:  "gpt-4 should not match",
			input: "gpt-4",
			want:  false,
		},
		{
			name:  "o3 should not match",
			input: "o3",
			want:  false,
		},
		{
			name:  "empty string should not match",
			input: "",
			want:  false,
		},
		{
			name:  "aide-o3 should match",
			input: "aide-o3",
			want:  true,
		},
		{
			name:  "aide-o3-pro should match",
			input: "aide-o3-pro",
			want:  true,
		},
		{
			name:  "aide-o4-mini should match",
			input: "aide-o4-mini",
			want:  true,
		},
		{
			name:  "aide-o5 should not match",
			input: "aide-o5",
			want:  false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got := OseriesModel.MatchString(tt.input)
			if got != tt.want {
				t.Errorf("azure.OseriesModel.MatchString(%q) = %v, want %v", tt.input, got, tt.want)
			}
		})
	}
}

