package azure

import (
	"testing"
)

func TestGetModelConfig(t *testing.T) {
	tests := []struct {
		name      string
		modelName string
		wantFound bool
	}{
		{
			name:      "GPT-4 Turbo model exists",
			modelName: "aide-gpt-4-turbo",
			wantFound: true,
		},
		{
			name:      "GPT-4o model exists",
			modelName: "aide-gpt-4o",
			wantFound: true,
		},
		{
			name:      "O3 model exists",
			modelName: "aide-o3",
			wantFound: true,
		},
		{
			name:      "DALL-E 3 model exists",
			modelName: "Dalle3",
			wantFound: true,
		},
		{
			name:      "Non-existent model",
			modelName: "non-existent-model",
			wantFound: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			config, found := GetModelConfig(tt.modelName)
			if found != tt.wantFound {
				t.Errorf("GetModelConfig(%s) found = %v, want %v", tt.modelName, found, tt.wantFound)
			}
			if found && config.Name == "" {
				t.Errorf("GetModelConfig(%s) returned config with empty name", tt.modelName)
			}
		})
	}
}

func TestIsPTUEnabled(t *testing.T) {
	tests := []struct {
		name   string
		model  string
		region string
		want   bool
	}{
		{
			name:   "PTU enabled for aide-gpt-4.1 in Japan",
			model:  "aide-gpt-4.1",
			region: "aideopenaijapaneast",
			want:   true,
		},
		{
			name:   "PTU disabled for aide-gpt-4.1 in other region",
			model:  "aide-gpt-4.1",
			region: "aideopenaieastus2",
			want:   false,
		},
		{
			name:   "PTU disabled for other model in Japan",
			model:  "aide-gpt-4o",
			region: "aideopenaijapaneast",
			want:   false,
		},
		{
			name:   "PTU disabled for other model in other region",
			model:  "aide-gpt-4o",
			region: "aideopenaieastus2",
			want:   false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := IsPTUEnabled(tt.model, tt.region); got != tt.want {
				t.Errorf("IsPTUEnabled(%s, %s) = %v, want %v", tt.model, tt.region, got, tt.want)
			}
		})
	}
}

func TestModelDeploymentConfigurations(t *testing.T) {
	// Test that all models have at least one region configured
	for modelName, deployment := range ModelDeployments {
		if len(deployment.Regions) == 0 {
			t.Errorf("Model %s has no regions configured", modelName)
		}

		// Check that model name matches the map key (except for special cases)
		if modelName != "Dalle3" && modelName != "dalle" && modelName != "dalleget" {
			if deployment.Name != modelName {
				t.Errorf("Model key %s doesn't match config name %s", modelName, deployment.Name)
			}
		}

		// Verify that embedding models don't have cap settings
		if deployment.IsEmbedding && deployment.CapGPT4 {
			t.Errorf("Embedding model %s should not have CapGPT4 enabled", modelName)
		}

		// Verify rate configuration for non-special models
		if !deployment.IsWhisper && !deployment.IsTTS && !deployment.IsRealtime && !deployment.IsDalle {
			if deployment.InputRate == 0 || deployment.OutputRate == 0 {
				t.Errorf("Model %s missing rate configuration", modelName)
			}
		}
	}
}

func TestRegionWeights(t *testing.T) {
	// Test that all region weights are positive
	for modelName, deployment := range ModelDeployments {
		for _, region := range deployment.Regions {
			if region.Weight <= 0 {
				t.Errorf("Model %s region %s has non-positive weight: %d", modelName, region.Name, region.Weight)
			}
			if region.Name == "" {
				t.Errorf("Model %s has a region with empty name", modelName)
			}
		}
	}
}

