package azure

import (
	"os"
)

// GetEnvOrDefault returns the value of an environment variable or a default value if not set
func GetEnvOrDefault(name, defaultValue string) string {
	if v := os.Getenv(name); v != "" {
		return v
	}
	return defaultValue
}

// GetEnvMust returns the value of an environment variable or panics if not set
func GetEnvMust(name string) string {
	v := os.Getenv(name)
	if v == "" {
		panic("Missing required environment variable: " + name)
	}
	return v
}

// GetAPIKeyForRegion returns the Azure OpenAI API key for a given region
func GetAPIKeyForRegion(region string) string {
	return os.Getenv("AZURE_OPAI_KEY_" + region)
}

