// BuildAOAIDispatchers creates all Azure OpenAI dispatchers based on model configurations
func BuildAOAIDispatchers(cfg *Config) map[string]*dispatcher.Dispatcher {
	dispatchers := make(map[string]*dispatcher.Dispatcher)

	for modelName, modelDeploy := range ModelDeployments {
		var endpoints []dispatcher.EndpointConfig

		// Build endpoint configurations for each region
		for _, region := range modelDeploy.Regions {
			key := os.Getenv("AZURE_OPAI_KEY_" + region.Name)
			if key == "" {
				continue // Skip regions without configured keys
			}

			endpointCfg := BuildAOAIEndpointConfig(region.Weight, region.Name, modelDeploy.Name, key)

			// Set NoReqLog for whisper models
			if modelDeploy.IsWhisper {
				endpointCfg.NoReqLog = true
			}

			endpoints = append(endpoints, endpointCfg)
		}

		if len(endpoints) == 0 {
			continue // Skip models with no configured endpoints
		}

		// Create appropriate SpendFn based on model type
		var spendFn common.SpendFn
		if modelDeploy.IsWhisper {
			spendFn = WhisperSpendFn(cfg.QuotaClient)
		} else if modelDeploy.IsTTS {
			ttsRate := 15000000 // Default TTS rate
			if modelName == "aide-tts-hd" {
				ttsRate = 30000000
			}
			spendFn = TTSSpendFn(ttsRate, cfg.QuotaClient)
		} else if modelDeploy.IsRealtime {
			spendFn = RealtimeSpendFn()
		} else if modelDeploy.IsDalle {
			if modelName == "dalleget" {
				// Special no-op spend function for dalleget
				spendFn = common.SpendFn{
					Pre: func(user, domain string, req *http.Request, body string) (estimate int, remain int, err error) {
						return 0, 1, nil
					},
					Post: func(user string, resp *http.Response, body string, preestimate int) (estimate int) {
						return 0
					},
					Alert: func(user string, logger *slog.Logger) {},
				}
			} else {
				spendFn = DalleSpendFn(modelDeploy.DalleRate, cfg.QuotaClient, cfg.SMTPServer)
			}
		} else {
			// Regular model
			encoder := cfg.CL100K
			if modelDeploy.UseP50k {
				encoder = cfg.P50K
			}
			spendFn = AoaiSpendFn(
				modelDeploy.Name,
				modelDeploy.CapGPT4,
				cfg.CrowdAPI,
				cfg.QuotaClient,
				cfg.SMTPServer,
				encoder,
				modelDeploy.InputRate,
				modelDeploy.OutputRate,
			)
		}

		dispatchers[modelName] = dispatcher.NewDispatcher(
			cfg.Transport,
			cfg.Logger,
			true,
			spendFn,
			endpoints,
			cfg.DailyResetController,
		)
	}

	return dispatchers
}

// BuildAOAIFallback creates the fallback endpoint for Azure OpenAI
func BuildAOAIFallback(cfg *Config) *dispatcher.Endpoint {
	key := os.Getenv("AZURE_OPAI_KEY_aideopenaieastus")
	if key == "" {
		return nil
	}

	endpointCfg := BuildAOAIEndpointConfig(0, "aideopenaieastus", "other", key)
	spendFn := AoaiSpendFn("", true, cfg.CrowdAPI, cfg.QuotaClient, cfg.SMTPServer, cfg.CL100K, 200, 150)

	return dispatcher.NewEndpoint(
		context.Background(),
		true,
		endpointCfg,
		cfg.Transport,
		cfg.Logger,
		spendFn,
		cfg.DailyResetController,
	)
}

// BuildAssistantsAPI creates the Assistants API endpoint
func BuildAssistantsAPI(cfg *Config) *dispatcher.Endpoint {
	resource := cfg.AssistantsAPIResource
	if resource == "" {
		resource = "aideopenaiaustraliaeast"
	}

	key := os.Getenv("AZURE_OPAI_KEY_" + strings.ReplaceAll(resource, "-", "_"))
	if key == "" {
		return nil
	}

	return dispatcher.NewEndpoint(
		context.Background(),
		false,
		dispatcher.EndpointConfig{
			URL:    fmt.Sprintf("https://%s.openai.azure.com", resource),
			Hdrs:   map[string]string{"api-key": key},
			Labels: []string{"aoai", "assistants", resource},
		},
		cfg.Transport,
		cfg.Logger,
		AssistantsSpendFn(cfg.QuotaClient, cfg.SMTPServer),
		cfg.DailyResetController,
	)
}

