	ctx, cancel := context.WithCancel(context.Background())
	globalDailyResetController.Start(ctx)
	var envfile string
	flag.StringVar(&envfile, "env-file", ".env", "Read in a file of environment variables")
	flag.Parse()

	logger := slog.New(slog.NewJSONHandler(os.Stdout, nil))

	if err := godotenv.Load(envfile); err == nil {
		logger.Info("loading config file", "file", envfile)
	}

	jwtKey := envmust("JWT_SECRET_KEY")
	domain := envstr("DOMAIN_CHECK", "oa")
	// (permMgr initialization moved later where crowdapi is available)

	if !common.Domain(domain).IsValid() {
		panic("DOMAIN_CHECK must be set to an airgapped domain")
	}

	userServiceURL := envstr("USER_SERVICE", "http://user-service:8080")

	smtpserver := "smtp.mediatek.com:25"

	if smtp := envstr("SMTP_SERVER", ""); smtp != "" {
		smtpserver = smtp
	}

	redisurl := envmust("REDIS_URL")

	capping := envstr("ENABLE_CAPPING", "true")

	var err error
	var crowdapi crowdadapter.Adapter

	rueidisopt := rueidis.ClientOption{InitAddress: []string{redisurl}}
	if strings.Contains(redisurl, "://") {
		rueidisopt = rueidis.MustParseURL(redisurl)
	}
	rueidisopt.CacheSizeEachConn = 64 * (1 << 20)
	rac, err := rueidisaside.NewClient(rueidisaside.ClientOption{ClientOption: rueidisopt})
	if err != nil {
		panic(err)
	}
	rc := rac.Client()

	switch envstr("CROWD_IMPL_TYPE", "crowd") {
	case "crowd":
		crowdapi, err = crowdadapter.NewCrowdAPI(envmust("CROWD_USERNAME"), envmust("CROWD_PASSWORD"), rac)
		if err != nil {
			panic(err)
		}
	case "none":
		crowdapi = &crowdadapter.Empty{}
	default:
		panic("unexpected CROWD_IMPL_TYPE")
	}

	permMgr := mtk.NewModelPermissionManager(jwtKey, crowdapi)
