		if err := ctx.ShouldBindJSON(&req); err != nil || req.Q == "" {
			ctx.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "missing q"})
			return
		}
		result, err := azurebingsearch.BingSearch(context.Background(), req.Q, azureBingCred)
		if err != nil {
			ctx.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
			return
		}
		ctx.AbortWithStatusJSON(http.StatusOK, gin.H{
			"project": lc.Project,
			"tokenId": lc.TokenID,
			"account": lc.Account,
			"domain":  lc.Domain,
