		logger.Info(ctx.Request.URL.Path, "req", common.AllowedReq{Uid: ctx.Query("uid"), Domain: ctx.Query("domain")}.ToString(), "hdrs", dispatcher.ToHeaderLogs(ctx.Request.Header, "", "", 0, 0, lc.Priority, 0, 0, 0))

		status, resp := common.CheckPIM(crowdapi, ctx.Query("domain"), ctx.Query("uid"))
