	app.POST("/charge", func(ctx *gin.Context) {
		uid := strings.ToUpper(ctx.Query("uid"))
		cost, err := strconv.Atoi(ctx.Query("cost"))
		if err != nil {
			ctx.AbortWithError(http.StatusBadRequest, err)
			return
		}
		if cost < 0 {
			ctx.AbortWithError(http.StatusBadRequest, errors.New("the cost should be >= 0"))
			return
		}
		remain, err := qc.Spend(ctx, uid, cost)
		if err != nil {
			ctx.AbortWithError(http.StatusInternalServerError, err)
