	app.POST("/setcap", func(ctx *gin.Context) {
		if ctx.Request.Header.Get("jwt-enc") != jwtKey {
			ctx.AbortWithStatusJSON(http.StatusForbidden, gin.H{"error": "invalid jwt-enc header"})
			return
		}
		uid := strings.ToUpper(ctx.Query("uid"))
		if uid == "" {
			ctx.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "uid is required"})
			return
		}
		cap, err := strconv.Atoi(ctx.Query("cap"))
		if err != nil {
			ctx.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "invalid cap value"})
			return
		}
		end, err := time.Parse("2006-01-02", ctx.Query("end"))
		if err != nil {
			ctx.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "invalid end format"})
			return
		}
		if err = qc.SetCap(ctx.Request.Context(), uid, cap, end.Unix()); err != nil {
			ctx.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": "failed to set cap"})
