		if err != nil {
			ctx.AbortWithError(http.StatusBadRequest, err)
			return
		}
		if err := qc.SetSessionCookie(ctx, userName, cookie, ttlSeconds); err != nil {
			ctx.AbortWithError(http.StatusInternalServerError, err)
			return
		}
		ctx.Status(http.StatusOK)
