	// davinciProcxyRouter is a Gin middleware function that handles routing and authentication for various API endpoints.
	// It performs the following main functions:
	//   - Validates domain access and user authentication
	//   - Enforces X-User-Id header requirement for cost allocation
	//   - Routes requests to appropriate services based on URL path:
	//     * LLM models (v3)
	//     * OpenAI services (including DALL-E)
	//     * Anthropic Claude models
	//     * Bing search
	//     * Azure Cognitive Services (vision, form recognition)
	//     * Speech services (text-to-speech, speech-to-text)
	//     * Gemini API endpoints
	//     * Davinci chat sharing
	//   - Performs PIM checks for certain endpoints
	//   - Enriches headers with required information
	//   - Handles timeout simulation for testing
	// Parameters:
	//   - c: Gin context containing the HTTP request and response information
	davinciProcxyRouter := func(c *gin.Context) {
		lc := c.MustGet("lc").(*common.LoggingContext)
		if lc.UID == nil {
			c.AbortWithStatusJSON(http.StatusForbidden, gin.H{"error": gin.H{
				"code":    strconv.Itoa(http.StatusForbidden),
				"message": "missing UID in logging context",
			}})
			return
		}
		if lc.Domain == "" {
			lc.Domain = "oa"
		}
		if (domain == "oa" && lc.Domain != domain) || ((common.IsAirgappedDomain(domain) || common.IsAirgappedDomain(c.Request.Header.Get("X-Domain"))) && !common.IsAirgappedDomain(lc.Domain)) {
			c.AbortWithStatusJSON(http.StatusForbidden, gin.H{"error": gin.H{
				"code":    strconv.Itoa(http.StatusForbidden),
				"message": fmt.Sprintf("token for domain %q is prohibited here", lc.Domain),
			}})
			return
		}
		endUser := *lc.UID
		if endUser == "" {
			c.AbortWithStatusJSON(http.StatusForbidden, gin.H{"error": gin.H{
				"code":    strconv.Itoa(http.StatusForbidden),
				"message": "The X-User-Id request header is now mandatory for cost allocation. Please set the parameter with \"mtkxxxxx\" for allocating to the end user's department or \"srv_xxxx\" for background jobs.",
			}})
			return
		}

		// Check if the request is for a specific endpoint that requires PIM check
		// and if the request method is POST, PUT, or GET with specific path prefix
		// If so, perform the PIM check and update the X-User-Id header if necessary
		// If the PIM check fails, abort the request with the appropriate status and response
		// If the PIM check passes, update the LoggingContext with the response data (Banwords)
		if !strings.HasPrefix(c.Request.URL.Path, "/llm/v3") &&
			(c.Request.Method == http.MethodPost ||
				c.Request.Method == http.MethodPut ||
				(c.Request.Method == http.MethodGet && strings.HasPrefix(c.Request.URL.Path, "/v7.0"))) {
			status, resp := common.CheckPIM(crowdapi, lc.Domain, endUser)
			if status != http.StatusOK {
				logger := allowedLogger.With("proj", lc.Project, "tkid", lc.TokenID, "acco", lc.Account, "rqid", c.Request.Header.Get("X-Request-ID"))
				logger.Info(c.Request.URL.Path, "req", "checkpim", "hdrs", dispatcher.ToHeaderLogs(c.Request.Header, "", "", 0, 0, lc.Priority, 0, 0, 0))
				logger.Info(c.Request.URL.Path, "resp", resp.ToString(), "hdrs", dispatcher.ToHeaderLogs(c.Request.Header, "", "", 0, 0, lc.Priority, 0, 0, 0))
				c.AbortWithStatusJSON(status, resp)
				return
			}
			if resp.UID != "" && resp.UID != endUser {
				c.Request.Header.Set("X-User-Id", resp.UID)
			}
			lc.Banwords = resp.Banwords
		}

		if strings.HasPrefix(c.Request.URL.Path, "/timeout") {
			duration, _ := time.ParseDuration(c.Query("dur"))
			time.Sleep(duration)
			c.JSON(http.StatusOK, gin.H{"duration": duration})
			return
		}

		if c.Request.Method != http.MethodDelete {

			if strings.HasPrefix(c.Request.URL.Path, "/model/anthropic") {
				modelId := strings.Split(c.Request.URL.Path, "/")[2]
				dispatcher, ok := awsdispatchers[modelId]
				if !ok {
					c.AbortWithStatusJSON(http.StatusNotFound, gin.H{"error": "model not found"})
					return
				}

				parts := strings.Split(c.Request.URL.Path, "/")
				c.Request.URL.Path = "/"
				if len(parts) > 3 {
					c.Request.URL.Path = "/" + strings.Join(parts[3:], "/")
				}

				dispatcher.ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				return
			}

			// route aws bedrock with bedrock arn
			if strings.HasPrefix(c.Request.URL.Path, "/model/") {
				modelKey := awsclaude.ParseBedrockModelKeyFromPath(c.Request.URL.Path)
				if ep, ok := bedrockendpoint[modelKey]; ok && ep != nil {
					ep.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
					return
				}
			}

			if strings.HasPrefix(c.Request.URL.Path, "/llm/v3/models/mtkresearch") {
				if err := enrichCSESHeaders(c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)), domain, lc.Project, lc.TokenID, lc.Payload); err != nil {
					c.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
					return
				}
				if mtkcreatetoken != "" {
					mtkcreate.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				} else {
					mtklmr.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				}
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/llm/v3/info") {
				if err := enrichCSESHeaders(c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)), domain, lc.Project, lc.TokenID, lc.Payload); err != nil {
					c.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
					return
				}
				mtklcore.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/llm/v3/models") {
				if err := enrichCSESHeaders(c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)), domain, lc.Project, lc.TokenID, lc.Payload); err != nil {
					c.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
					return
				}
				mtklcore.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/openai/") {
				if strings.HasPrefix(c.Request.URL.Path, "/openai/assistants") {
					assistantsAPI.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
					return
				}
				if strings.HasPrefix(c.Request.URL.Path, "/openai/threads") {
					assistantsAPI.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
					return
				}
				if strings.HasPrefix(c.Request.URL.Path, "/openai/files") {
					assistantsAPI.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
					return
				}

				deployment := extractURLSlug(c.Request.URL.Path, 3)
				switch deployment {
				case "generations:submit": // DALL·E submit
					deployment = "dalle"
				case "images": // DALL·E get
					deployment = "dalleget"
				}
				if d, ok := aoaidispatchers[deployment]; ok {
					// Check and set default api-version only if needed
					// See the reference: https://learn.microsoft.com/en-us/azure/ai-services/openai/reference
					q := c.Request.URL.Query()
					if q.Get("api-version") == "" {
						q.Set("api-version", azure.OpenAIAPIVersion)
						c.Request.URL.RawQuery = q.Encode()
					}
					d.ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				} else {
					if aoaifallback == nil || aoaifallback.Proxy() == nil {
						c.AbortWithStatus(http.StatusNotFound)
						return
					}
					aoaifallback.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				}
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/vision") {
				if c.Request.ContentLength > 1024*1024*10 {
					c.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": gin.H{
						"code": strconv.Itoa(http.StatusBadRequest),
					}})
					return
				}
				azurecognitive.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/formrecognizer") {
				azurecognitive.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/api/texttospeech") {
				customvoice.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/speech/recognition/conversation/cognitiveservices") {
				sttxy.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
				return
			} else if strings.HasPrefix(c.Request.URL.Path, "/v1/projects/mtk-aide-gemini-poc/locations") {
				// Remove API version from path if present (e.g., v1beta1 after region)
				// Original: /v1/projects/mtk-aide-gemini-poc/locations/global/v1beta1/publishers/...
				// Target:   /v1/projects/mtk-aide-gemini-poc/locations/global/publishers/...
				originalPath := c.Request.URL.Path
				pathAfterLocations := originalPath[len("/v1/projects/mtk-aide-gemini-poc/locations/"):]

				// Find first slash to get region name
				if firstSlash := strings.Index(pathAfterLocations, "/"); firstSlash > 0 {
					region := pathAfterLocations[:firstSlash]
					remainingPath := pathAfterLocations[firstSlash+1:]

					// Check if the next segment is an API version (v1beta1, v2beta1, v1alpha, v2alpha2, etc.)
					if nextSlash := strings.Index(remainingPath, "/"); nextSlash > 0 {
						potentialVersion := remainingPath[:nextSlash]
						if matched, _ := regexp.MatchString("^v\\d+(alpha|beta)\\d*$", potentialVersion); matched {
							// Reconstruct path without the API version
							c.Request.URL.Path = "/v1/projects/mtk-aide-gemini-poc/locations/" + region + "/" + remainingPath[nextSlash+1:]
						}
					}

					// Match against region
					if endpoint, exists := geminis[region]; exists {
						endpoint.Proxy().ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
						return
					}
				}
				c.AbortWithStatus(http.StatusNotFound)
				return
			} else {
				deployment := extractURLSlug(c.Request.URL.Path, 1)

				if d, ok := aoaidispatchers[deployment]; ok {
					c.Request.URL.Path = strings.TrimPrefix(c.Request.URL.Path, "/"+deployment)
					d.ServeHTTP(c.Writer, c.Request.WithContext(context.WithValue(c.Request.Context(), "lc", lc)))
					return
				}
			}

		}
		if strings.HasPrefix(c.Request.URL.Path, "/davinci/share-chat") {
			davinciShareProd(c)
			return
		} else if strings.HasPrefix(c.Request.URL.Path, "/davinci-io/share-chat") {
			davinciShareStag(c)
			return
		}
		c.AbortWithStatus(http.StatusNotFound)
	}

	enableLLMOverride := envBool("ENABLE_OVERRIDE_LLM", false)
	if enableLLMOverride {
		initOverriddenMiddleware()
		app.NoRoute(header.Options, jwtVeriHandler, overrideMiddleware, davinciProcxyRouter)
	} else {
		app.NoRoute(header.Options, jwtVeriHandler, davinciProcxyRouter)
	}

	go func() {
		srv := &http.Server{
			Addr:              ":" + envstr("SERVER_PORT", "8080"),
			Handler:           app.Handler(),
			ReadHeaderTimeout: time.Minute,
			ReadTimeout:       0, // intentionally 0
			WriteTimeout:      0, // intentionally 0
			MaxHeaderBytes:    http.DefaultMaxHeaderBytes,
		}
		if err := srv.ListenAndServe(); err != http.ErrServerClosed {
			panic(err)
		}
	}()

	poker := poker.DavinciPoker{
		Username:    envstr("IA_USERNAME", ""),
		Password:    envstr("IA_PASSWORD", ""),
		BotTenantID: envstr("BOT_TENANT_ID", ""),
		BotID:       envstr("BOT_ID", ""),
		BotPassword: envstr("BOT_PASSWORD", ""),
	}
	go poker.RunLoop(30 * time.Minute)

	sigs := make(chan os.Signal, 1)
	signal.Notify(sigs, syscall.SIGINT, syscall.SIGTERM)
	<-sigs
	cancel()
	time.Sleep(5 * time.Second)
}

