		SMTPServer:            smtpserver,
		CL100K:                cl100k,
		P50K:                  p50k,
		DailyResetController:  globalDailyResetController,
		AssistantsAPIResource: envstr("ASSISTANTS_API_RESOURCE", "aideopenaiaustraliaeast"),
		AzureCognitiveHost:    envstr("AZURE_COGNITIVE_HOST", "https://aide-cognitive-multi.cognitiveservices.azure.com"),
		AzureCognitiveKey:     envmust("AZURE_COGNITIVE_KEY"),
