// UserTokenRequest represents a request to generate a user token with a specified expiration time.
// ExpirationHours specifies the number of hours the token will be valid for and is a required field.
type UserTokenRequest struct {
	ExpirationHours int             `json:"expirationHours" binding:"required"`
	Payload         *map[string]any `json:"payload"`
}

type UserLoginRequest struct {
	UserTokenRequest
	Username string `json:"username" binding:"required"`
	Password string `json:"password" binding:"required"`
}


type ResourceConfig struct {
	URL    string `json:"url"`
	Key    string `json:"key"`
	Secret string `json:"secret"`
	Region string `json:"region"`
	Weight int    `json:"weight"`
}

type Provider string

var (
	ClaudeV3Provider = Provider("anthropic.claude-v3")
	OpenAIProvider   = Provider("openai")
)

type DispatchConfig struct {
	Provider Provider `json:"provider"`
	Model    string   `json:"model"`
	Price    struct {
		Prompt     int `json:"prompt"`
		Completion int `json:"completion"`
		DalleN     int `json:"dalleN"`
	} `json:"price"`
	Resources map[string]ResourceConfig `json:"resources"`
}

func checkInCrowdGroup(crowdapi crowdadapter.Adapter, list, uid string) (int, InListResp) {
	if _, ok := crowdapi.(*crowdadapter.Empty); ok {
		return http.StatusOK, InListResp{In: true}
	}
	if uid == "" || list == "" {
		return http.StatusBadRequest, InListResp{Reason: "uid and list should not be empty"}
	}
	if strings.Contains(uid, "@") {
		users, err := crowdapi.SearchUsers(context.Background(), "email="+uid, crowdadapter.DefaultLimit)
		if err != nil {
			return http.StatusInternalServerError, InListResp{Reason: err.Error()}
		}
		if len(users) == 0 {
			return http.StatusNotFound, InListResp{Reason: fmt.Sprintf("user not found: %q", uid)}
		}
		uid = users[0].Name
	}
	groups, err := crowdapi.GetUserNestedGroups(context.Background(), uid, crowdadapter.DefaultLimit)
	if err != nil {
		return http.StatusInternalServerError, InListResp{Reason: err.Error()}
	}
	for _, group := range groups {
		if group.Name == list {
			return http.StatusOK, InListResp{In: true}
		}
	}
	return http.StatusOK, InListResp{In: false}
}

func URL(target string) *url.URL {
	purl, err := url.Parse(target)
	if err != nil {
