var buffers sync.Pool

func NewTeeBufReader(r io.ReadCloser, done func(string)) *TeeBufReader {
	return &TeeBufReader{r: r, w: buffers.Get().(*bytes.Buffer), done: done}
}

type TeeBufReader struct {
	r    io.ReadCloser
	w    *bytes.Buffer
	done func(buf string)
}

func (t *TeeBufReader) Read(p []byte) (n int, err error) {
	if t.r == nil {
		return 0, io.EOF
	}
	n, err = t.r.Read(p)
	if n > 0 {
		if n, err := t.w.Write(p[:n]); err != nil {
			return n, err
		}
	}
	return
}

func (t *TeeBufReader) Close() (err error) {
	bs := t.w.Bytes()
	t.done(*(*string)(unsafe.Pointer(&bs)))
	t.w.Reset()
	buffers.Put(t.w)
	if t.r != nil {
		return t.r.Close()
	}
	return nil
}

type InListResp struct {
	In     bool   `json:"in"`
	Reason string `json:"reason"`
}

func (p InListResp) ToString() string {
	bs, _ := json.Marshal(p)
	return *(*string)(unsafe.Pointer(&bs))
}

type InListReq struct {
	Uid  string `json:"uid"`
	List string `json:"list"`
}

func (p InListReq) ToString() string {
	bs, _ := json.Marshal(p)
	return *(*string)(unsafe.Pointer(&bs))
}

var proxyUsers map[string]bool

func init() {
	buffers = sync.Pool{New: func() any { return bytes.NewBuffer(make([]byte, 0, 4096)) }}
	proxyUsers = make(map[string]bool, 20)
	for _, u := range strings.Split(os.Getenv("PROXY_USERS"), ",") {
