func checkInCrowdGroup(crowdapi crowdadapter.Adapter, list, uid string) (int, InListResp) {
	if _, ok := crowdapi.(*crowdadapter.Empty); ok {
		return http.StatusOK, InListResp{In: true}
	}
	if uid == "" || list == "" {
		return http.StatusBadRequest, InListResp{Reason: "uid and list should not be empty"}
	}
	if strings.Contains(uid, "@") {
		users, err := crowdapi.SearchUsers(context.Background(), "email="+uid, crowdadapter.DefaultLimit)
		if err != nil {
			return http.StatusInternalServerError, InListResp{Reason: err.Error()}
		}
		if len(users) == 0 {
			return http.StatusNotFound, InListResp{Reason: fmt.Sprintf("user not found: %q", uid)}
		}
		uid = users[0].Name
	}
	groups, err := crowdapi.GetUserNestedGroups(context.Background(), uid, crowdadapter.DefaultLimit)
	if err != nil {
		return http.StatusInternalServerError, InListResp{Reason: err.Error()}
	}
	for _, group := range groups {
		if group.Name == list {
			return http.StatusOK, InListResp{In: true}
		}
	}
	return http.StatusOK, InListResp{In: false}
}

func URL(target string) *url.URL {
