import env from '@kosko/env';
import { Deployment } from 'kubernetes-models/apps/v1/Deployment';
import { HorizontalPodAutoscaler } from 'kubernetes-models/autoscaling/v2';
import { Service } from 'kubernetes-models/v1/Service';
import type { DeployConfig, UnifiedMCPEnvironment } from './types';

if (!env.env || Array.isArray(env.env)) {
  throw new Error(
    'please specify kosko generate -e <env> or do not use base env',
  );
}

const params = env.component('unified-mcp') as unknown as UnifiedMCPEnvironment;

if (Object.keys(params).length === 0) {
  throw new Error('please specify a correct env');
}

for (const key of ['MCP_SERVICE_NAME', 'MCP_CONTAINER_PORT']) {
  if (!process.env[key]) {
    throw new Error(`${key} is required by your mcp services`);
  }
}

if (
  !process.env.MCP_SERVICE_IMAGE_STAG &&
  !process.env.MCP_SERVICE_IMAGE_PROD
) {
  throw new Error(
    'MCP_SERVICE_IMAGE_STAG or MCP_SERVICE_IMAGE_PROD is required by mcp service',
  );
}

const name = process.env.MCP_SERVICE_NAME || '';
const labels = { app: name };
const metadata = {
  name: name,
  namespace: params.namespace,
};

function deploy(config: DeployConfig) {
  const image = config.image;
  const containerPort = parseInt(process.env.MCP_CONTAINER_PORT || '0', 10);
  const additionalEnvVars = Object.keys(process.env)
    .filter((key) => key.startsWith('MCPENV_'))
    .map((key) => ({
      name: key,
      value: process.env[key] || '',
    }));

  const de = new Deployment({
    metadata: metadata,
    spec: {
      selector: {
        matchLabels: labels,
      },
      template: {
        metadata: {
          labels: labels,
        },
        spec: {
          containers: [
            {
              name,
              image,
              imagePullPolicy: 'Always',
              ports: [
                {
                  name: 'http',
                  containerPort: containerPort,
                },
              ],
              env: [
                {
                  name: 'TRANSPORT_MODE',
                  value: 'http',
                },
                ...additionalEnvVars,
              ],
            },
          ],
          imagePullSecrets: params.imagePullSecrets,
        },
      },
    },
  });

  const hpa = new HorizontalPodAutoscaler({
    metadata: metadata,
    spec: {
      maxReplicas: 5,
      metrics: [
        {
          type: 'Resource',
          resource: {
            name: 'cpu',
            target: {
              type: 'Utilization',
              averageUtilization: 70,
            },
          },
        },
      ],
      scaleTargetRef: {
        apiVersion: 'apps/v1',
        kind: 'Deployment',
        name: metadata.name,
      },
    },
  });

  const svc = new Service({
    metadata: metadata,
    spec: {
      selector: labels,
      ports: [
        {
          port: 80,
          protocol: 'TCP',
          targetPort: 'http',
        },
      ],
    },
  });

  return [de, svc, hpa];
}

export default [
  ...params.deployments
    .filter((c: DeployConfig) => !!c.image)
    .map((c: DeployConfig) => deploy(c)),
];

